import ProjectList from '../components/ProjectList'
import useFetch from '../hooks/useFetch'
import { useNavigate } from "react-router-dom";
import { useAuthContext } from "../hooks/useAuthContext"
import { Reroute } from '../components/Reroute';




const Home = () => {
    Reroute()
    const {user} = useAuthContext()
    username = user
    const title = "Projects"
    const {data: projects, isPending, errorMessage} = useFetch('/api/projects');
    const navigate = useNavigate();


    const toDelete = () => {
        navigate('/delete');
    }
   /* const handleDelete = (id) => {
        const newProjects = projects.filter(project => project.id !== id);
        setProjects(newProjects);

    }*/



    return ( 
        <div className="home">
            {errorMessage && <div>{errorMessage}</div>}
            {isPending && <div>Loading...</div>}
            {projects && <div style={{display: 'flex', justifyContent:'right'}}><button onClick={toDelete}>Delete A Project</button></div>}
            {projects && <ProjectList projects={projects.filter((project) => project.username === "alex")} title={title}  /*handleDelete={handleDelete}*//>}
           { /*<ProjectList projects={projects.filter((project) => project.author === "alex")} title={title}/>*/}
        </div>


     );
}

export default Home;
